mapper.update:
update tbl_board set
	title = #{title},
	content = #{content},
	writer = #{writer}
where bno = #{bno}

mapper.select:
select * from
		(select rownum rnum, a.*
		 from (select * from tbl_board
		 	   order by bno desc) a
		 )
where rnum >= #{startRow} and rnum <= #{endRow}

select * from
		(select rownum rnum, a.*
		 from (select * from tbl_board
		 	   order by bno desc) a
		 )
where rnum >= #{startRow} and rnum <= #{endRow}

mapper.update:
update tbl_board set
	replycnt = replycnt + #{amount}
where bno = #{bno}

update tbl_board set
	viewcnt = viewcnt + 1
where bno = #{bno}
