mapper.select:
select * from
		(select rownum rnum, a.*
		 from (select * from tbl_reply
		 	   where bno = #{bno}
		 	   order by rno desc) a
		 )
where rnum >= #{cri.startRow} and rnum <= #{cri.endRow}

select count(*) 
from tbl_reply
where bno = #{bno}

select bno
from tbl_reply
where rno = #{rno}
